import{a as e,F as t,j as i,b as o}from"./app-f45dd868.js";import{H as n}from"./UserendLayout-9b659bcf.js";function s(){return e(t,{children:i(n,{children:[e(o,{title:"One Year Course"}),e(t,{children:e("div",{className:"professional-course-details",children:e("div",{className:"container",children:i("div",{className:"row",children:[i("div",{className:"text-center",children:[e("img",{src:"image/logo-black.png",alt:""}),e("span",{children:"Website: http://www.evolutionimageworks.com"}),e("span",{children:"Phone: 7895660773, 9412054231, 0135-2767234"})]}),e("h2",{children:"1 YEAR DIPLOMA IN PROFESSIONAL PHOTOGRAPHY"}),i("h3",{children:["Course Duration : 1 Year ",e("span",{children:"(Academy Timings: 10am to 5pm)"})]}),e("h4",{children:"Course Content:"}),i("ul",{children:[e("li",{children:" Basics of Photography"}),e("li",{children:" Outdoor Photography"}),e("li",{children:" Fine Art & Abstract Photography"}),e("li",{children:" Documentary	Photography"}),e("li",{children:" Photojournalism"}),e("li",{children:" Nature and Landscape Photography"}),e("li",{children:" Studio and Outdoor Lighting"}),e("li",{children:" Wedding and Pre-Wedding Photography"}),e("li",{children:" Flash Photography (outdoor and Indoor) (using Flash with sunlight , fill or as a main light)"}),e("li",{children:" Fashion Photography (outdoor and Indoor) "}),i("li",{children:[" Photoshop :",i("ul",{children:[e("li",{children:"Image Editing "}),e("li",{children:" ICC Profile"}),e("li",{children:" File Format"}),e("li",{children:"Raw, Tiff and Jpeg Files"}),e("li",{children:"Color correction"})]})]}),e("li",{children:"Product/Still Life or Table Top Photograph"}),e("li",{children:" Food Photography"}),e("li",{children:"Architectural Photography"})]}),i("div",{children:[e("h4",{children:"BASICS OF PHOTOGRAPHY"}),e("p",{children:"In this fundamental course, students will identify basic photographic tools.What is photography?Different types of Photography, including the proper use of various camera systems and light meters, Basic colors or primary colors. Physics of camera i.e. how camera works and different types of camera, Camera handling, Physics of optics and its nature how lenses work and different types of Lense. What is shutter and its speed? What is aperture and how does it work?, What is ISO and metering, How do they work? White balance, Focusing points, File Format RAW or JPEG, Focusing mode,Depth of field, Exposure,Multiple exposure - Getting the nuts and bolts of the most important session of all, Perspective and Composition, Different types of shots/ Angle, Impact of weather conditions, Light and its nature, fall off of light and different sources and types of lights.......etc"})]}),i("div",{children:[e("h4",{children:"TECHNIQUES OF PHOTOGRAPHY"}),e("p",{children:"Black and White Photography, Camera Setting,Long exposure, night photography, Panning, tracking and use of Tripods, Shooting in daylight with manual and auto settings. Over powering the sunlight for high end fashion shoots."})]}),i("div",{children:[e("h4",{children:"PRINCIPLES OF DESIGN AND COMPOSITION"}),e("p",{children:" Basics color principles, including line, shape, hue, texture, form, value and intensity are studied.Students investigate color as seen in black and white and its relationship to composition through harmony and contrast in a variety of formats and media. Students will also learn the usage of classical rules of composition to make a visual statement."})]}),i("div",{children:[e("h4",{children:"METERING"}),e("p",{children:"Understanding the secret of exposure control. Learning and exploiting all the tools available for correct exposure. Understanding light and shade. Students will explore light reflectivity in relation to the tonality of the image. This section involves extensive use of the DSLR’s, and learning the use of digital software’s for Black & White conversion, rendering and multi K3 inkjet printing."})]}),i("div",{children:[e("h4",{children:"OUTDOOR PHOTOGRAPHY"}),e("p",{children:" In outdoor Photography, students will visit many locations and will be taught what should be the subject, how to pick the subject and how to shoot different things, in a very creative way."})]}),i("div",{children:[e("h4",{children:"FINE ART PHOTOGRAPHY"}),e("p",{children:"Fine art Photography, which can be used as photo décor for homes/offices. Indoor/outdoor photography using black & white or color, using various formats to create visually appealing images of practically any subject. The subject can be as varied as landscapes, nature, ruins, artifacts, sunsets, etc"})]}),i("div",{children:[e("h4",{children:"NATURE AND LANDSCAPES"}),e("p",{children:"Students learn how to photograph the beauty of nature and its landscapes with an emphasis on natural and available light and the right techniques to bring forth the best image. Field trips, at differing times of the day complete this course. Helps develop images for both fine art and stock."})]}),i("div",{children:[e("h4",{children:" STUDIO LIGHTS"}),e("p",{children:"In-depth understanding of artificial lights i.e. studio flashlights, halogen, tungsten, Point light source, Reflectors, Wide light sources, Light banks, Umbrellas, soft boxes, honeycombs, snoots, etc. Understanding light direction, throw of light, soft light, contrasty or hard light, the Light cage, etc. understanding color temperature light quality, advantages and problems."})]}),i("div",{children:[e("h4",{children:"FLASH	PHOTOGRAPHY"}),e("p",{children:"Students will be taught to use the portable flash for portraits and fashion, fill in flash photography using slow shutter. Use of advanced flash techniques like various diffused and bounced lighting. Photography using diffused and warm flash. Lighting setup for group, couple and individual in studio and at location. Classic portrait using halogen and portable flash."})]}),i("div",{children:[e("h4",{children:"COMPRESSION	CURVE"}),e("p",{children:"Setting accurate light ratios for offset printing using the Compression Curve. The use of Histogram for accurate exposure in fashion photography."})]}),i("div",{children:[e("h4",{children:" DIGITAL	PHOTOGRAPHIC	PRODUCTION"}),e("p",{children:"This course focuses on the role of the computer as a necessary tool for the professional photographer. Emphasis is on the development of advanced retouching and manipulation skills as these relate to the use of photographic imagery in two dimensional presentation media."})]}),i("div",{children:[e("h4",{children:"AVAILABLE	LIGHT	PEOPLE	PHOTOGRAPHY"}),e("p",{children:"Introduction to Outdoor Fashion and Portrait Lighting using Diffuser, Reflector, Mirror etc., Five portrait lighting outdoor."})]}),i("div",{children:[e("h4",{children:"PORTRAITURE"}),e("p",{children:"Concept of Portrait photography. Tools and requirement: Studio, camera, lenses, film etc. Basic Portrait lighting: Main light, fill, hair, background light. Light ratio. Studio and/or location photography. Groups and outdoor portraits. Light metering for portraiture."})]}),i("div",{children:[e("h4",{children:"FASHION"}),e("p",{children:"A comprehensive study of advanced traditional and contemporary portrait techniques. Corrective and glamorous make-up. Classical lighting set ups. Fashion lighting set ups, Film Noir Lighting. Use of various backgrounds. Posing and creative techniques. Designed for students who are considering a major interest in fashion photography."})]}),i("div",{children:[e("h4",{children:" FASHION	PHOTOGRAPHY"}),e("p",{children:" This course provides an overview of the fashion photography industry.  The theory/practical of collaborative work among a design team will be executed. Features a mock ad campaign shoot demo featuring traditional/contemporary fashion wear. Understanding fashion trends for editorial."})]}),i("div",{children:[e("h4",{children:"PHOTOGRAPHIC	FASHION	ACCESSORIES"}),e("p",{children:" Students will be taught the lighting and professional techniques to creatively and accurately shoot fashion accessories like purses, cosmetics, perfumes, belts, etc."})]}),i("div",{children:[e("h4",{children:"PRODUCT/STILL	LIFE	PHOTOGRAPHY"}),e("p",{children:"Concept of Product/Still Life photography. The tools and requirements, studio, camera, lenses, etc. Texture lighting, lighting for highlights, three-dimensional lighting. Basic tabletop set ups. Critical focusing for edge-to-edge sharpness. Lighting for square, round, spherical, and cylindrical objects. Lighting for artwork/picture copying. Shadow-less product photography and creating color vignettes for backgrounds. Intensive hands-on practical examples designed to present difficult subjects like transparent objectsglass, reflective and metal objects, different types of jewellery, food, TV /computer monitors, medical photography, macro photography, U.V. light photography et"})]}),i("div",{children:[e("h4",{children:"ARCHITECTURE PHOTOGRAPHY"}),e("p",{children:"Students explore the nature and function of commercial corporate, industrial and architecture photography. They will examine the cameras and materials necessary in corporate architecture and industrial photography. Annual report photographs with story telling qualities will be stressed"})]}),i("div",{children:[e("h4",{children:"ADVERTISING [PEOPLE]"}),e("p",{children:"Conceptual people photography. The entire process from selection of models, production, pre, post of an advertising shoot. Shooting people at location and on a studio set for an advertising campaign."})]}),i("div",{children:[e("h4",{children:"DIGITAL	IMAGING	EDITING"}),e("p",{children:" ICC	Profiles,		File	Formats,	digital	image	structure,	Photo	editing,			Photoshop	Tools	– Image editing,Background	removing,	Useful	retouching	tools.Use	Adjustment	Layers.	Levels	and Curves,Contrast	control,	color	correction	/	enhancement,	sharpening,	Retouching	/	basic	digital enhancement."})]}),i("div",{children:[e("h4",{children:"SOFTWARE"}),e("p",{children:" In depth hands on training on the practical usage of Digital Imaging software viz. Adobe Photoshop, Genuine fractals, etc. Fundamentals to in-depth working."})]}),i("div",{children:[e("h4",{children:"COLOR	MANAGEMENT	AND	PHOTO	ENHANCEMENT"}),e("p",{children:"Advanced color management techniques, Photo retouching, enhancing and optimizing, Displacement mapping, Essential post production techniques will be taught with demo and practical."})]}),i("div",{children:[e("h4",{children:"INCLUDES"}),e("p",{children:"Brand new practical syllabus, Course material, three models with make up and hair dressing on one to one basis per student (two half day shifts and one full day shift). Plus a minimum of three models shoot along with the faculty and entire class as part of curriculum.. Understanding Beauty shoot conceptualization. Outdoor fashion shoot (lecture and project). Production fashion shoot (lecture and project). Creative fashion shoot (lecture and project). Expert class on how to build a rapport with the models, Assistance in developing your portfolio and business marketing."})]}),i("div",{children:[e("h4",{children:"PROFESSIONAL	PORTFOLIO	DEVELOPMENT"}),e("p",{children:"An evaluation by students and instructors of projects for the purpose of preparing a professional portfolio. Students and faculty work closely in an effort to bring out the student's individual qualities, strengths and skills.The Students will have spent nearly 2 month to develop and complete their portfolio so as to present their skills to ad agencies to solicit freelance professional work and/or seek short time internship with established photographers to further specialize in their chosen field of photography."})]}),i("div",{children:[e("h4",{children:"BASIC REQUIREMENT"}),i("ul",{children:[e("li",{children:"D-SLR Camera"}),e("li",{children:"Wide angle Lens and Zoom Lens"}),e("li",{children:"Tripod"}),e("li",{children:"Flash"}),e("li",{children:"Trigger"}),e("li",{children:"Shutter Release"}),e("li",{children:"Spirit Level….etc"})]})]})]})})})})]})})}export{s as default};
